--
-- Дамп данных таблицы `news`
--

INSERT INTO `news` (`id`, `title`, `text`, `author`, `date`, `time`, `image`) VALUES
(1, 'Чемпионат Европы: первые медали', 'В испанском городе Санта-Сусанна стартовал очередной чемпионат Европы по бодибилдингу, и атлеты уже успели отметиться наградами. Так, Сергей Поврезнюк из Алтайского края завоевал «золото» в категории мастеров 40 -49 лет и в категории мужчин до 75 кг. Алексей Юсупов выиграл в номинации «классический бодибилдинг» до 171 см. Иван Пережогин стал третьим в «классике» до 175 см. Отлично начали и украинцы. Сергей Ющенко победил в катгории мужчин до 70 кг, а Василий Ремизов стал третьим в номинации мастеров 40-49 лет до 90 кг.', 'Admin', '2016-09-18', '2016-09-19 13:47:08', 'news1.jpg'),
(2, 'Кубок по бодибилдингу: первые результаты', 'Прошел первый день Кубка  по бодибилдингу и фитнесу в Краснодаре. Стали известны первые результаты. Так в категории мастеров 40-49 лет до 90 кг победил Сергей Сорокин (Астрахань). Сергей Поврезнюк ( Кемерово) – второй, Владимир Тимошин ( Санкт-Петербург) – третий. В категории мастеров 40-49 лет свыше 90 кг Денис Бурмистров (Москва) – первый, Владислав Фатьянов (Калининград) – второй. В категории мастеров старше 60 лет победил Александр Яшанькин. Абсолютным победителем среди мастеров стал Денис Бурмистров. Победителем в категории до 75 кг среди юниоров стал Егор Воробьев (Санкт-Петербург), а в категории юниоров свыше 75 кг первенствовал Павел Котлубаев (Красноярск). Он же выиграл абсолютное первенство. Вашему вниманию предлагаются фото от Романа Джабелова.', 'Admin', '2015-05-15', '2016-09-19 13:47:03', 'news2.jpg');

